const axios = require("axios");
const { Card } = require("../models/index");

class ControllerCard {
  static async fetchCard(req, res, next) {
    try {
      let option = {
          order: [['elixir', 'asc']]
      }
      if (+req.query.sort === 1) {
        option.order = [['elixir', 'desc']]
      } else {
        option.order = [['elixir', 'asc']]
      }
      let cardData = await Card.findAll(option);
      if (cardData.length) {
        res.status(200).json(cardData);
      } else {
        let { data } = await axios({
          method: "get",
          url: "https://api.clashroyale.com/v1/cards",
          headers: {
            Authorization:
              "Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzUxMiIsImtpZCI6IjI4YTMxOGY3LTAwMDAtYTFlYi03ZmExLTJjNzQzM2M2Y2NhNSJ9..4-G8yZmarredfPJYCFq2QZw_rJPrWe9xIL0qiBOGdcaaIRl1YUDhai8M00FIwYeWkMW-ZmDTV6W_W_lJeuWlRw",
          },
        });
        let dataCard = data.items.map((el) => {
          Card.create({
            name: el.name,
            iconUrl: el.iconUrls.medium,
          });
        });
        res.status(200).json(dataCard);
      }
    } catch (error) {
      next(error)
    }
  }
}

module.exports = ControllerCard;
