const { default: axios } = require("axios");
const { Card, Player } = require("../models/index");

class ControllerPlayer {
  static async getPlayerById(req, res, next) {
    try {
      console.log(req.body.tag, 'initaggg')
      let playerData = await Player.findOne({
        where: {
          tag: `#${req.body.tag}`,
        },
      });
      if (playerData) {
        res.status(200).json(playerData);
      } else {
        let { data } = await axios({
            method: 'get',
            url: `https://api.clashroyale.com/v1/players/%23${req.body.tag}`,
            headers: {
                Authorization:
                  "Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzUxMiIsImtpZCI6IjI4YTMxOGY3LTAwMDAtYTFlYi03ZmExLTJjNzQzM2M2Y2NhNSJ9..4-G8yZmarredfPJYCFq2QZw_rJPrWe9xIL0qiBOGdcaaIRl1YUDhai8M00FIwYeWkMW-ZmDTV6W_W_lJeuWlRw",
              },
        })
        let dataPlayer = await Player.create({
            tag: data.tag,
            name: data.name,
            level: data.expLevel,
            trophies: data.trophies,
            wins: data.wins,
            losses: data.losses,
            battlecount: data.battleCount
        })
        res.status(200).json(dataPlayer)
      }
    } catch (error) {
      console.log(error)
        next(error)
    }
  }
}

